/* 增加cookie name表示cookie名 value表示cookie值 day表示有效期 */
function setCookie(name,value,day){var oDate=new Date();oDate.setDate(oDate.getDate()+day);document.cookie=name+"="+value+";expires="+oDate}function getCookie(name){var strCookie=document.cookie;var arrCookie=strCookie.split("; ");for(var i=0;i<arrCookie.length;i+=1){var arr=arrCookie[i].split("=");if(arr[0]==name){return arr[1]}}}function removeCookie(name){setCookie(name,1,-1)}function drag(ele){ele.onmousedown=function(e){var evt=e||event;var disX=evt.offsetX;var disY=evt.offsetY;document.onmousemove=function(e){var evt=e||event;var _left=evt.clientX-disX;var _top=evt.clientY-disY;ele.style.left=_left+"px";ele.style.top=_top+"px"};document.onmouseup=function(){document.onmousemove=null;document.onmouseup=null}}}function tab(arr,classname){for(var i=0;i<arr.length;i+=1){arr[i].onclick=function(){for(var j=0;j<arr.length;j+=1){arr[j].className=""}this.className=classname}}}function get(ele){if(ele.charAt(0)=="."){var newEle=ele.slice(1);return document.getElementsByClassName(newEle)}else if(ele.charAt(0)=="#"){var newEle=ele.slice(1);return document.getElementById(newEle)}else{return document.getElementsByTagName(ele)}}function getStyle(ele,attr){if(ele.currentStyle){return ele.currentStyle[attr]}else{return getComputedStyle(ele,false)[attr]}}function startMove(ele,json,fn){clearInterval(ele.timer);ele.timer=setInterval(function(){var flag=true;for(var attr in json){if(attr=="opacity"){var iCur=Math.round(getStyle(ele,attr)*100)}else{var iCur=parseInt(getStyle(ele,attr))}var iTarget=json[attr];var iSpeed=(iTarget-iCur)/10;iSpeed=iSpeed>0?Math.ceil(iSpeed):Math.floor(iSpeed);if(attr=="opacity"){ele.style.opacity=(iCur+iSpeed)/100;ele.style.filter="alpha(opacity="+(iCur+iSpeed)+")"}else{ele.style[attr]=iCur+iSpeed+"px"}if(iCur!=iTarget){flag=false}}if(flag){clearInterval(ele.timer);if(fn){fn()}}},30)}function huandong(ele,target){clearInterval(ele.timer);ele.timer=setInterval(function(){var step=(target-ele.offsetLeft)/10;step=step>0?Math.ceil(step):Math.floor(step);ele.style.left=ele.offsetLeft+step+"px";if(ele.offsetLeft==target){clearInterval(ele.timer)}},30)}function yunsu(ele,target){clearInterval(ele.timer);var speed=target>ele.offsetLeft?10:-10;ele.timer=setInterval(function(){ele.style.left=ele.offsetLeft+speed+"px";if(Math.abs(target-ele.offsetLeft)<=Math.abs(speed)){ele.style.left=target+"px";clearInterval(ele.timer)}},30)}function toTop(target){clearInterval(timer);timer=setInterval(function(){var scrollTop=document.documentElement.scrollTop||document.body.scrollTop;var step=(target-scrollTop)/10;step=step>0?Math.ceil(step):Math.floor(step);document.documentElement.scrollTop=document.body.scrollTop=scrollTop+step;if(Math.abs(target-scrollTop)<=Math.abs(step)){document.documentElement.scrollTop=document.body.scrollTop=target;clearInterval(timer)}},30)}function getMinIndex(arr){var minVal=Math.min.apply(null,arr);var minIndex=arr.indexOf(minVal);return minIndex}function getMaxIndex(arr){var maxVal=Math.max.apply(null,arr);var maxIndex=arr.indexOf(maxVal);return maxIndex}function lunbo(){var banner=document.getElementById("banner");var ul=document.getElementById("imgWrap");var ol=document.getElementById("focusWrap");var btn=document.getElementById("btn");var cloneLi=ul.children[0].cloneNode(true);ul.appendChild(cloneLi);var aFocus=ol.children;var aLi=ul.children;var timer=null;var imgWidth=ul.children[0].offsetWidth;timer=setInterval(animate,3000);var i=0;for(var j=0;j<aFocus.length;j+=1){aFocus[j].index=j;aFocus[j].onmouseover=function(){i=this.index-1;animate()}}banner.onmouseover=function(){btn.style.display="block";clearInterval(timer)};banner.onmouseout=function(){btn.style.display="none";timer=setInterval(animate,3000)};btn.children[0].onclick=function(){i=i-2;animate()};btn.children[0].onselectstart=function(){return false};btn.children[1].onclick=function(){animate()};btn.children[1].onselectstart=function(){return false};function animate(){i+=1;if(i==aLi.length){i=1;ul.style.left=0}if(i==-1){i=aLi.length-2;ul.style.left= -imgWidth*(aLi.length-1)+"px"}startMove(ul,{"left": -imgWidth*i});for(var j=0;j<aFocus.length;j+=1){aFocus[j].id=""}if(i==aFocus.length){aFocus[0].id="cur"}else{aFocus[i].id="cur"}}}